import json
import requests
import subprocess
import sys
import time
import os
import random
import tempfile
import unittest


class Tests(unittest.TestCase):
    middle_port = 33333

    def setUp(self):
        self.processes = []
        self.cookie = '%x' % random.getrandbits(64)
        self.tmp_dir = tempfile.mkdtemp()
        with open(os.path.join(self.tmp_dir, 'test.txt'), 'w') as f:
            f.write(self.cookie)

    def tearDown(self):
        self.stop_processes()

    def stop_processes(self):
        for cmd, p in self.processes:
            print("terminating %s" % ' '.join(cmd))
            p.terminate()
        for cmd, p in self.processes:
            p.wait()

    def configure(self):
        response = requests.post("http://127.0.0.1:4080/tunnels/", data=json.dumps({
            "port": self.middle_port,
        	"from": {
        		"public_key": "ssh-rsa ",
        		"user": "alice",
        	},
        	"to": {
        		"public_key": "ssh-rsa ",
        		"user": "bob",
        	}
        }), headers={
            'Content-Type': 'application/json'
        })
        self.assertEqual(response.status_code, 200)

    def spawn(self, command, **kwargs):
        print("spawn %r" % (command,))
        p = subprocess.Popen(command, **kwargs)
        self.processes.append((command, p))
        return p

    def test_successful_tunnel(self):
        try:
            basedir = os.path.abspath(os.path.join(os.path.dirname(__file__), '..'))
            self.spawn([os.path.join(basedir, 'sshub'), '--key-file=tests/test_rsa'], cwd=basedir)
            time.sleep(1)
            self.configure()
            subprocess.call('curl http://127.0.0.1:4080/tunnels/ | jq .', shell=True)

            self.spawn(['php', '-S', 'localhost:8080', '--docroot', self.tmp_dir])
            self.spawn(['ssh', '-NL', '9080:localhost:%s' % self.middle_port, '-p', '4022', '-i', 'alice', 'alice@localhost'])
            self.spawn(['ssh', '-NR', '%s:localhost:8080' % self.middle_port, '-p', '4022', '-i', 'bob', 'bob@localhost'])
            time.sleep(1)
            subprocess.call('curl http://127.0.0.1:4080/tunnels/ | jq .', shell=True)

            response = requests.get('http://127.0.0.1:9080/test.txt')
            result = response.content.decode('ascii')
            subprocess.call('curl http://127.0.0.1:4080/tunnels/ | jq .', shell=True)
        finally:
            self.stop_processes()
        self.assertEqual(result, self.cookie)
